   field2 VARCHAR(255) NOT NULL,
   field3 INTEGER NOT NULL,
   field4 INTEGER NOT NULL,
   PRIMARY KEY (id));

CREATE TABLE Extention4
  (id BIGINT NOT NULL GENERATED ALWAYS AS IDENTITY,
   field1 VARCHAR(255) NOT NULL,
